mapper.select:
SELECT t.USER_NAME, t.USER_ID, t.USER_TYPE, t.ORG_ID, t.PHONE, t.STATE_DATE, t.PASSWORD
FROM USER t
where 1 = 1

    and t.USER_ID = #{userId}


    and t.USER_TYPE = #{userType}


    and t.USER_NAME = #{userName}


    and t.ORG_ID = #{orgId}

mapper.update:
update USER set
          ORG_ID = #{orgId}
        , USER_NAME = #{userName}
        , USER_TYPE = #{userType}
        , STATE_DATE = #{stateDate}
        , PHONE = #{phone}
        , PASSWORD = #{password}
where USER_ID = #{userId}
